import React, {useRef, useEffect, useState} from 'react'
import videoTiktok from "../../music/videoTiktok.mp4"

const colors = ["red", "green", "blue", "pink", "yellow"];

function Blog() {
  const [color, setColor] = useState("red");
  const inputRef = useRef();
  console.log(inputRef);
  const videoRef = useRef();

  useEffect(() => {
    console.log(inputRef);
    inputRef.current.focus();
  }, []);

  const play = () => {
    videoRef.current.play();
  }

  const pause = () => {
    videoRef.current.pause();
  }

  return (
    <>
    <h1>useRef Hook</h1>
    <input placeholder='Enter name: ...' ref={inputRef}/>

    <hr />

    <video src={videoTiktok} style={{height: 300}} ref={videoRef} ></video>
    <button onClick={play}>Play</button>
    <button onClick={pause}>Pause</button>

    <hr />

    <div style={{height : 200, width : 200, border: "1px solid #333"}}></div>
    <button style={{backgroundColor : "#fff"}}>Random Background Color</button>
    </>
  )
}

export default Blog
