  const handleUpdateTitle = () => {

  }

  const handleDelete = () => {

  }

  return (
    <>
      <h2>TodoList App</h2>

      <input placeholder='Enter title...'/>
      <button>Add</button>

      <ul>
        {todos.length === 0 && (
          <li>Không có công việc nào trong danh sách</li>
        )}
        {todos.length > 0 && (
          todos.map(todo => (
            <li key={todo.id}>
              <input type={checkbox} checked={todo.status} onChange={() => handleToggleStatus(todo.id)}/>
              <span className={todo.status ? "active" : ""}>{todo.title}</span>
              <button onClick={() => handleUpdateTitle(todo.id)}>Update</button>
              <button onClick={() => handleDelete(todo.id)}>Delete</button>
            </li>
          ))
        )}
      </ul>
    </>
  )
}

export default TodoList
