import axios from "axios";
import React, { useState, useEffect, useRef } from "react";

const API_URL = "http://localhost:8090/api/v1/todos";
function TodoList() {
	const [todos, setTodos] = useState([]);
	const [title, setTitle] = useState("");
	const titleRef = useRef();

	useEffect(() => {
