import { useState } from "react";
import Menu from "./component/menu/Menu";
function App() {
  const [isShow, setIsShow] = useState(true);
  const toggle = () => {
    setIsShow(!isShow);
  }

	return (
		<>
			{/* <Content /> */}

			<hr />

			{/* <Post /> */}

			<hr />
      {isShow && <Menu /> }
      <button onClick={toggle}>Toggle</button>
		</>
	);
}

export default App;
